import { useState } from "react";
import axios from "axios";

function MatchScore() {
  const [candidateBio, setCandidateBio] = useState("");
  const [jobDesc, setJobDesc] = useState("");
  const [result, setResult] = useState("");

  const handleCheck = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/match/match-score", {
        candidateBio,
        jobDesc,
      });
      setResult(res.data.result);
    } catch (err) {
      alert("Failed to get match score");
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>AI Match Score Checker</h2>
      <textarea
        placeholder="Enter candidate bio"
        rows="5"
        cols="50"
        value={candidateBio}
        onChange={(e) => setCandidateBio(e.target.value)}
      /><br /><br />
      <textarea
        placeholder="Enter job description"
        rows="5"
        cols="50"
        value={jobDesc}
        onChange={(e) => setJobDesc(e.target.value)}
      /><br /><br />
      <button onClick={handleCheck}>Get Match Score</button>
      <div style={{ marginTop: "1rem", whiteSpace: "pre-line" }}>
        {result && <strong>Result:</strong>}<br />
        {result}
      </div>
    </div>
  );
}

export default MatchScore;
