import { useState } from "react";
import axios from "axios";

function MatchScore() {
  const [bio, setBio] = useState("");
  const [jobDesc, setJobDesc] = useState("");
  const [score, setScore] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/match/match-score", {
        bio,
        jobDesc
      });
      setScore(res.data.matchScore);
    } catch (err) {
      alert("Error calculating match score");
      setScore(null);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>üîç Job ‚Üî Applicant Match Score</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          rows="4"
          cols="60"
          placeholder="Enter applicant bio..."
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          required
        />
        <br /><br />
        <textarea
          rows="4"
          cols="60"
          placeholder="Enter job description..."
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
          required
        />
        <br /><br />
        <button type="submit">Get Match Score</button>
      </form>

      {score !== null && (
        <div style={{ marginTop: "1.5rem", fontSize: "1.2rem" }}>
          ‚úÖ <strong>Match Score:</strong> {score} / 100
        </div>
      )}
    </div>
  );
}

export default MatchScore;
