import { useState } from "react";
import axios from "axios";

function JobSearch() {
  const [query, setQuery] = useState({ tech: "", profile: "" });
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setQuery({ ...query, [e.target.name]: e.target.value });
  };

  const handleSearch = async () => {
    setLoading(true);
    setMessage("");
    try {
      const res = await axios.get("http://localhost:5000/api/jobs/search", {
        params: query,
      });
      setResults(res.data);
      if (res.data.length === 0) {
        setMessage("No matching jobs found.");
      }
    } catch (err) {
      setMessage("Error fetching jobs.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Search Jobs</h2>
      <input
        name="tech"
        placeholder="Tech (e.g., React)"
        onChange={handleChange}
        value={query.tech}
      />
      <input
        name="profile"
        placeholder="Profile (e.g., developer)"
        onChange={handleChange}
        value={query.profile}
      />
      <button onClick={handleSearch}>Search</button>

      {loading && <p>Loading...</p>}
      {message && <p>{message}</p>}

      <ul>
        {results.map((job) => (
          <li key={job._id}>
            <b>{job.profile}</b> ({job.exp} yrs) â€“ {job.desc}
            <br />
            Techs: {job.techs.join(", ")}
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default JobSearch;
