import { useState } from "react";
import { ethers } from "ethers";
import axios from "axios";

function PostJob() {
  const [mode, setMode] = useState("paid"); // "paid" or "free"
  const [form, setForm] = useState({
    _id: "",
    profile: "",
    desc: "",
    exp: "",
    techs: "",
  });
  const [walletAddress, setWalletAddress] = useState("");
  const [paid, setPaid] = useState(false);
  const [loading, setLoading] = useState(false);
  const platformFee = "0.00001"; // in AMOY for Polygon Amoy Testnet
  const receiverAddress = "0x0A499e74e5C49e111a9D86Ae6A5f1306982782Bf"; // Replace with your actual receiver address

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const connectWallet = async () => {
    if (!window.ethereum) return alert("MetaMask not detected");

    try {
      setLoading(true);
      // First ensure we're on Polygon Amoy Testnet
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x13882" }], // Polygon Amoy chainId
      });

      const provider = new ethers.BrowserProvider(window.ethereum);
      const accounts = await window.ethereum.request({ 
        method: "eth_requestAccounts" 
      });
      setWalletAddress(accounts[0]);

      // Verify balance
      const balance = await provider.getBalance(accounts[0]);
      console.log("AMOY Balance:", ethers.formatEther(balance));
    } catch (err) {
      if (err.code === 4902) {
        // Network not added, let's add it
        await addPolygonAmoyNetwork();
        await connectWallet(); // Retry connection
      } else {
        alert("Wallet connection failed: " + err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const addPolygonAmoyNetwork = async () => {
    try {
      await window.ethereum.request({
        method: "wallet_addEthereumChain",
        params: [{
          chainId: "0x13882",
          chainName: "Polygon Amoy Testnet",
          nativeCurrency: {
            name: "AMOY",
            symbol: "AMOY",
            decimals: 18
          },
          rpcUrls: ["https://rpc-amoy.polygon.technology"],
          blockExplorerUrls: ["https://www.oklink.com/amoy"]
        }]
      });
    } catch (err) {
      alert("Failed to add Polygon Amoy network: " + err.message);
    }
  };

  const payPlatformFee = async () => {
  try {
    setLoading(true);
    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();

    // Get current network details
    const network = await provider.getNetwork();
    console.log("Network:", network);

    // Get fee data
    const feeData = await provider.getFeeData();
    console.log("Fee Data:", {
      gasPrice: feeData.gasPrice?.toString(),
      maxFeePerGas: feeData.maxFeePerGas?.toString(),
      maxPriorityFeePerGas: feeData.maxPriorityFeePerGas?.toString()
    });

    // Calculate platform fee in wei
    const value = ethers.parseEther(platformFee);

    // Get gas estimate (use a reasonable default if estimation fails)
    let gasEstimate;
    try {
      gasEstimate = await provider.estimateGas({
        from: walletAddress,
        to: receiverAddress,
        value: value
      });
    } catch (estimateError) {
      console.warn("Gas estimation failed, using default:", estimateError);
      gasEstimate = ethers.toBigInt(21000); // Default gas limit for simple transfers
    }

    // Check balance
    const balance = await provider.getBalance(walletAddress);
    const estimatedCost = (feeData.maxFeePerGas || feeData.gasPrice) * gasEstimate + value;

    if (balance < estimatedCost) {
      throw new Error(
        `Insufficient funds. Need ${ethers.formatEther(estimatedCost)} AMOY but have ${ethers.formatEther(balance)}`
      );
    }

    // Build transaction
    const txRequest = {
      to: receiverAddress,
      value: value,
      gasLimit: gasEstimate,
    };

    // Use appropriate fee structure based on network support
    if (network.chainId === 80002n) { // Polygon Amoy uses EIP-1559
      txRequest.maxFeePerGas = feeData.maxFeePerGas;
      txRequest.maxPriorityFeePerGas = feeData.maxPriorityFeePerGas;
    } else {
      txRequest.gasPrice = feeData.gasPrice;
    }

    console.log("Transaction Request:", txRequest);

    // Send transaction
    const tx = await signer.sendTransaction(txRequest);
    console.log("Transaction sent:", tx.hash);

    // Wait for confirmation
    const receipt = await tx.wait();
    console.log("Transaction confirmed:", receipt);

    if (receipt.status === 1) {
      alert("Payment Successful");
      setPaid(true);
    } else {
      throw new Error("Transaction failed");
    }
  } catch (err) {
    console.error("Payment Error:", err);
    let errorMessage = err.reason || err.message;

    // Handle specific MetaMask errors
    if (err.code === "ACTION_REJECTED") {
      errorMessage = "Transaction was rejected by user";
    } else if (err.code === -32603) {
      errorMessage = "Network error. Please try again or check your gas settings.";
    }

    alert(`Payment failed: ${errorMessage}`);
  } finally {
    setLoading(false);
  }
};

  // Styles remain the same
