    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
    width: "400px",
    textAlign: "center",
  };

  const headingStyle = {
    marginBottom: "1rem",
    color: "#4b0082",
  };

  const inputStyle = {
    width: "100%",
    padding: "10px",
    marginBottom: "1rem",
    border: "1px solid #ccc",
    borderRadius: "5px",
  };

  const textareaStyle = {
    width: "100%",
    padding: "10px",
    height: "80px",
    marginBottom: "1rem",
    border: "1px solid #ccc",
    borderRadius: "5px",
  };

  const btnStyle = {
    marginBottom: "1rem",
    padding: "10px 20px",
    backgroundColor: "#6a1b9a",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "15px",
    opacity: loading ? 0.7 : 1,
    pointerEvents: loading ? "none" : "auto",
  };

  const submitStyle = {
    width: "100%",
    padding: "12px",
    backgroundColor: "#4b0082",
    color: "white",
    border: "none",
    borderRadius: "6px",
    fontSize: "16px",
    cursor: "pointer",
    opacity: loading ? 0.7 : 1,
    pointerEvents: loading ? "none" : "auto",
  };

  const activeBtn = {
    ...btnStyle,
    backgroundColor: "#4b0082",
    marginRight: "10px",
  };

  const inactiveBtn = {
    ...btnStyle,
    backgroundColor: "#bbb",
    marginRight: "10px",
  };

  return (
    <div style={containerStyle}>
      <div style={formBox}>
        <h2 style={headingStyle}>Post a New Job</h2>

        <div style={{ marginBottom: "1rem" }}>
          <button 
            onClick={() => setMode("paid")} 
            style={mode === "paid" ? activeBtn : inactiveBtn}
            disabled={loading}
          >
            Post With Payment
          </button>
          <button 
            onClick={() => setMode("free")} 
            style={mode === "free" ? activeBtn : inactiveBtn}
            disabled={loading}
          >
            Post Without Payment (Demo)
          </button>
        </div>

        {mode === "paid" && !walletAddress && (
          <button onClick={connectWallet} style={btnStyle} disabled={loading}>
            {loading ? "Connecting..." : "Connect Wallet"}
          </button>
        )}
        {mode === "paid" && walletAddress && !paid && (
          <button onClick={payPlatformFee} style={btnStyle} disabled={loading}>
            {loading ? "Processing..." : `Pay ${platformFee} AMOY`}
          </button>
        )}
        {mode === "paid" && paid && (
          <p style={{ color: "green", fontWeight: "bold" }}>✔ Payment Complete</p>
        )}

        <form onSubmit={handleSubmit}>
          <input 
            name="_id" 
            placeholder="Job ID" 
            value={form._id}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <input 
            name="profile" 
            placeholder="Profile" 
            value={form.profile}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <textarea 
            name="desc" 
            placeholder="Description" 
            value={form.desc}
            onChange={handleChange} 
            required 
            style={textareaStyle} 
          />
          <input 
            name="exp" 
            type="number" 
            placeholder="Experience (years)" 
            value={form.exp}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <input 
            name="techs" 
            placeholder="Techs (comma separated)" 
            value={form.techs}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <button type="submit" style={submitStyle} disabled={loading}>
            {loading ? "Submitting..." : "Post Job"}
          </button>
        </form>
      </div>
    </div>
  );
