import { useState } from "react";
import { ethers } from "ethers";
import axios from "axios";

function PostJob() {
  const [mode, setMode] = useState("paid"); // "paid" or "free"
  const [form, setForm] = useState({
    _id: "",
    profile: "",
    desc: "",
    exp: "",
    techs: "",
  });
  const [walletAddress, setWalletAddress] = useState("");
  const [paid, setPaid] = useState(false);
  const [loading, setLoading] = useState(false);
  const platformFee = "0.00001"; // in AMOY for Polygon Amoy Testnet
  const receiverAddress = "0x0A499e74e5C49e111a9D86Ae6A5f1306982782Bf"; // Replace with your actual receiver address

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const connectWallet = async () => {
    if (!window.ethereum) return alert("MetaMask not detected");

    try {
      setLoading(true);
      // First ensure we're on Polygon Amoy Testnet
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x13882" }], // Polygon Amoy chainId
      });

      const provider = new ethers.BrowserProvider(window.ethereum);
      const accounts = await window.ethereum.request({ 
        method: "eth_requestAccounts" 
      });
      setWalletAddress(accounts[0]);

      // Verify balance
      const balance = await provider.getBalance(accounts[0]);
      console.log("AMOY Balance:", ethers.formatEther(balance));
    } catch (err) {
      if (err.code === 4902) {
        // Network not added, let's add it
        await addPolygonAmoyNetwork();
        await connectWallet(); // Retry connection
      } else {
        alert("Wallet connection failed: " + err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const addPolygonAmoyNetwork = async () => {
    try {
      await window.ethereum.request({
        method: "wallet_addEthereumChain",
        params: [{
          chainId: "0x13882",
          chainName: "Polygon Amoy Testnet",
          nativeCurrency: {
            name: "AMOY",
            symbol: "AMOY",
            decimals: 18
          },
          rpcUrls: ["https://rpc-amoy.polygon.technology"],
          blockExplorerUrls: ["https://www.oklink.com/amoy"]
        }]
      });
    } catch (err) {
      alert("Failed to add Polygon Amoy network: " + err.message);
    }
  };

  const payPlatformFee = async () => {
    try {
      setLoading(true);
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();

      // Get fee data (replaces getGasPrice in ethers v6)
      const feeData = await provider.getFeeData();

      // Get gas estimate
      const gasEstimate = await provider.estimateGas({
        from: walletAddress,
        to: receiverAddress,
        value: ethers.parseEther(platformFee)
      });

      // Check balance
      const balance = await provider.getBalance(walletAddress);
      const requiredBalance = gasEstimate * feeData.gasPrice + ethers.parseEther(platformFee);

      if (balance < requiredBalance) {
        throw new Error(`Insufficient funds. Need ${ethers.formatEther(requiredBalance)} AMOY but have ${ethers.formatEther(balance)}`);
      }

      const tx = await signer.sendTransaction({
        to: receiverAddress,
        value: ethers.parseEther(platformFee),
        gasLimit: gasEstimate,
        maxFeePerGas: feeData.maxFeePerGas,
        maxPriorityFeePerGas: feeData.maxPriorityFeePerGas
      });

      await tx.wait();
      alert("Payment Successful");
      setPaid(true);
    } catch (err) {
      alert("Payment failed: " + (err.reason || err.message));
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (mode === "paid" && !paid) {
      return alert("Please pay the platform fee first");
    }

    const payload = {
      ...form,
      exp: parseInt(form.exp),
      techs: form.techs.split(",").map((t) => t.trim()),
      payerAddress: mode === "paid" ? walletAddress : null
    };

    try {
      setLoading(true);
      const res = await axios.post("http://localhost:5000/api/jobs", payload);
      alert(res.data.message);
      // Reset form after successful submission
      setForm({
        _id: "",
        profile: "",
        desc: "",
        exp: "",
        techs: "",
      });
      if (mode === "paid") setPaid(false);
    } catch (err) {
      alert("Error: " + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  // Styles remain the same
  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    background: "linear-gradient(to right, #e0c3fc, #8ec5fc)",
  };

  const formBox = {
    background: "#fff",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
    width: "400px",
    textAlign: "center",
  };

  const headingStyle = {
    marginBottom: "1rem",
    color: "#4b0082",
  };

  const inputStyle = {
    width: "100%",
    padding: "10px",
    marginBottom: "1rem",
    border: "1px solid #ccc",
    borderRadius: "5px",
  };

  const textareaStyle = {
    width: "100%",
    padding: "10px",
    height: "80px",
    marginBottom: "1rem",
    border: "1px solid #ccc",
    borderRadius: "5px",
  };

  const btnStyle = {
    marginBottom: "1rem",
    padding: "10px 20px",
    backgroundColor: "#6a1b9a",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "15px",
    opacity: loading ? 0.7 : 1,
    pointerEvents: loading ? "none" : "auto",
  };

  const submitStyle = {
    width: "100%",
    padding: "12px",
    backgroundColor: "#4b0082",
    color: "white",
    border: "none",
    borderRadius: "6px",
    fontSize: "16px",
    cursor: "pointer",
    opacity: loading ? 0.7 : 1,
    pointerEvents: loading ? "none" : "auto",
  };

  const activeBtn = {
    ...btnStyle,
    backgroundColor: "#4b0082",
    marginRight: "10px",
  };

  const inactiveBtn = {
    ...btnStyle,
    backgroundColor: "#bbb",
    marginRight: "10px",
  };

  return (
    <div style={containerStyle}>
      <div style={formBox}>
        <h2 style={headingStyle}>Post a New Job</h2>

        <div style={{ marginBottom: "1rem" }}>
          <button 
            onClick={() => setMode("paid")} 
            style={mode === "paid" ? activeBtn : inactiveBtn}
            disabled={loading}
          >
            Post With Payment
          </button>
          <button 
            onClick={() => setMode("free")} 
            style={mode === "free" ? activeBtn : inactiveBtn}
            disabled={loading}
          >
            Post Without Payment (Demo)
          </button>
        </div>

        {mode === "paid" && !walletAddress && (
          <button onClick={connectWallet} style={btnStyle} disabled={loading}>
            {loading ? "Connecting..." : "Connect Wallet"}
          </button>
        )}
        {mode === "paid" && walletAddress && !paid && (
          <button onClick={payPlatformFee} style={btnStyle} disabled={loading}>
            {loading ? "Processing..." : `Pay ${platformFee} AMOY`}
          </button>
        )}
        {mode === "paid" && paid && (
          <p style={{ color: "green", fontWeight: "bold" }}>✔ Payment Complete</p>
        )}

        <form onSubmit={handleSubmit}>
          <input 
            name="_id" 
            placeholder="Job ID" 
            value={form._id}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <input 
            name="profile" 
            placeholder="Profile" 
            value={form.profile}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <textarea 
            name="desc" 
            placeholder="Description" 
            value={form.desc}
            onChange={handleChange} 
            required 
            style={textareaStyle} 
          />
          <input 
            name="exp" 
            type="number" 
            placeholder="Experience (years)" 
            value={form.exp}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <input 
            name="techs" 
            placeholder="Techs (comma separated)" 
            value={form.techs}
            onChange={handleChange} 
            required 
            style={inputStyle} 
          />
          <button type="submit" style={submitStyle} disabled={loading}>
            {loading ? "Submitting..." : "Post Job"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default PostJob;
