import { useState } from "react";
import axios from "axios";

function PostJob() {
  const [form, setForm] = useState({
    _id: "",
    profile: "",
    desc: "",
    exp: "",
    techs: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...form,
      exp: parseInt(form.exp),
      techs: form.techs.split(',').map(tech => tech.trim())
    };

    try {
      const res = await axios.post("http://localhost:5000/api/jobs", payload);
      alert(res.data.message);
    } catch (err) {
      alert("Error: " + err.response.data.error);
    }
  };

  return (
    <div>
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <input name="_id" placeholder="Job ID" onChange={handleChange} required /><br />
        <input name="profile" placeholder="Profile" onChange={handleChange} required /><br />
        <textarea name="desc" placeholder="Description" onChange={handleChange} required /><br />
        <input name="exp" type="number" placeholder="Experience (years)" onChange={handleChange} required /><br />
        <input name="techs" placeholder="Techs (comma separated)" onChange={handleChange} required /><br />
        <button type="submit">Post Job</button>
      </form>
    </div>
  );
}

export default PostJob;
