import { useState } from "react";
import axios from "axios";

function JobSearch() {
  const [query, setQuery] = useState({ tech: "", profile: "" });
  const [results, setResults] = useState([]);

  const handleChange = (e) => {
    setQuery({ ...query, [e.target.name]: e.target.value });
  };

  const handleSearch = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/jobs/search", { params: query });
      setResults(res.data);
    } catch (err) {
      alert("Error fetching jobs");
    }
  };

  return (
    <div>
      <h2>Search Jobs</h2>
      <input name="tech" placeholder="Tech (e.g., React)" onChange={handleChange} />
      <input name="profile" placeholder="Profile (e.g., developer)" onChange={handleChange} />
      <button onClick={handleSearch}>Search</button>

      <ul>
        {results.map((job) => (
          <li key={job._id}>
            <b>{job.profile}</b> ({job.exp} yrs) â€“ {job.desc}
            <br />
            Techs: {job.techs.join(", ")}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default JobSearch;
